media_type.script:
try {
//	Zabbix.Log(127, 'Pushover webhook script value='+value);

	params = JSON.parse(value),
	req = new CurlHttpRequest(),
	fields = {};

	req.AddHeader('Content-Type: application/json');

	fields.token= params.token;
	fields.user = params.user;


       /*
       If a device name is not specified for a user, or the specified device name is no longer enabled/valid,
       notifications will be sent to all active devices for that user to avoid losing messages.
       Messages may be addressed to multiple specific devices by joining them with a comma (such as device=iphone,nexus5).
       fields.device = params.device;
       */
	fields.title =  params.title;
	fields.message =  params.message;
        fields.url = params.url+"/tr_events.php?triggerid="+params.triggerid+"&eventid="+params.eventid;
        fields.url_title = params.url_title;
        fields.priority = params.priority;
	var resp = req.Post('https://api.pushover.net/1/messages.json',
		JSON.stringify(fields)
	);

	if (req.Status() != 200) {
		throw JSON.parse(resp).errors[0];
	}

	resp = JSON.parse(resp);
        return "OK";

} catch (error) {
	Zabbix.Log(127, 'Pushover notification failed : '+error);
        throw 'Pushover notification failed : '+error;
}
